            <div className="checkout">Add to Bag</div>
          </div>
        </Info>
      </Wrap>
    </Container>
  );
};

const Container = styled.div`
  height: 100%;
  width: 100%;
`;

const Wrap = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
`;

const Gallery = styled.div`
  height: 100vh;
  width: 100%;
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  padding: 25px;
  grid-gap: 15px;
  gap: 15px;

  img {
    max-width: 100%;
    object-fit: contain;
  }

  div {
    background-color: rgb(0, 0, 0, 0.1);
  }
`;

const Info = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 25px;

  .product {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  #model,
  #price,
  .description {
    font-size: 16px;
  }

  #name {
    font-size: 32px;
    line-height: 32px;
    padding: 0;
    margin: 0;
  }

  .description {
    line-height: 1.75;
    padding: 20px;
  }

  .checkout {
    background-color: black;
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 30px;
    font-size: 16px;
    border: none;
    height: 60px;
    width: 80%;

    &:hover {
      background-color: rgb(0, 0, 0, 0.8);
    }
  }
`;

export default Product;
